// TODO: implement chart component
import React, { useMemo } from "react";
import { getTotalsByField, renderCustomizedLabel } from '../utils/utils.tsx';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Pie,
  PieChart,
} from "recharts";
import TranslucentBarChart from "./TranslucentBarChart.tsx";

const DenialChart = ({ data }) => {
    const distributionByDepartment = useMemo(() => getTotalsByField({data: data, field: "department"}), [data]);
    console.log(distributionByDepartment);

    return (
        <>
            <TranslucentBarChart chartData={distributionByDepartment} />
            <ResponsiveContainer width="100%" maxWidth={700} height={400}>
                <PieChart style={{ width: '100%' }}>
                    <Pie
                        data={distributionByDepartment}
                        labelLine={false}
                        label={renderCustomizedLabel}
                        fill="#8884d8"
                        dataKey="count"
                        nameKey="department"
                    />
                </PieChart>
            </ResponsiveContainer>
        </>
    )
};

export default DenialChart;
