import React from 'react';
import { ChartData, renderCustomizedLabel } from '../utils/utils.tsx';
import {
  ResponsiveContainer,
  Pie,
  PieChart,
  PieProps,
  Sector
} from "recharts";

const TranslucentPieChart: React.FC<ChartData> = ({ chartData, selectedDepartment, dataKey, nameKey }) => {

    const CustomShape = (props: PieProps) => {
        const fill = props.type == selectedDepartment ? "#d3d3f2" : "#8884d8";
        console.log({props});
        return <Sector {...props} fill={fill} />;
    };

    return (
        <ResponsiveContainer width="100%" maxWidth={500} height={250}>
            <PieChart style={{ width: '100%' }}>
                <Pie
                    data={chartData}
                    labelLine={false}
                    label={renderCustomizedLabel}
                    fill="#8884d8"
                    dataKey={dataKey}
                    nameKey={nameKey}
                    shape={CustomShape}
                />
            </PieChart>
        </ResponsiveContainer>
    );
};

export default TranslucentPieChart;
