type DenialsData = {
    id: string,
    department: string,
    amount: number,
    reason: string,
    date: string,
    payer: string,
}

type ChartData = {
    data: DenialsData[],
    field: keyof DenialsData,
}

const getTotalsByField = ({data, field}: ChartData) => {
   return data.reduce<Record<string, number>>((acc, item) => {
    const key = String(item[field])
    acc[key] = (acc[key] || 0) + 1;
    return acc
   }, {}); 
}
