import { ApolloProvider, ApolloClient, InMemoryCache, gql, useQuery } from '@apollo/client';
import React, { useState, useMemo } from 'react';
import DenialChart from './components/DenialChart.tsx';
import { DenialsData } from './utils/utils.tsx';

const client = new ApolloClient({ uri: 'http://localhost:4000/', cache: new InMemoryCache() });
const DENIALS_QUERY = gql`query { denials { id department amount reason date } }`;

function Dashboard() {
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const { loading, error, data } = useQuery(DENIALS_QUERY);

  const denials = data?.denials;
  const departments: string[] = useMemo(() => Array.from(new Set(denials.map((d: DenialsData) => d.department))), [selectedDepartment]);
  const filteredDenials: DenialsData[] = useMemo(() => denials.filter((d: DenialsData) => d.department == selectedDepartment), [selectedDepartment]); 

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <>
      <h1>Denial Dashboard</h1>
      <DenialChart data={data.denials}/>

      <div style={{ marginBottom: 16 }}>
        <select
          value={selectedDepartment}
          onChange={(e) => setSelectedDepartment(e.target.value)}
        >
          <option value="">All Departments</option>
          {departments.map((dept) => (
            <option key={dept} value={dept}>
              {dept}
            </option>
          ))}
        </select>

        {selectedDepartment && (
          <button
            style={{ marginLeft: 8 }}
            onClick={() => setSelectedDepartment("")}
          >
            Clear Filter
          </button>
        )}
      </div>

      <table>
        <thead><tr><th>ID</th><th>Dept</th><th>Amount</th><th>Reason</th><th>Date</th></tr></thead>
        <tbody>
          {filteredDenials?.map((d:any)=>(
            <tr key={d.id}>
              <td>{d.id}</td><td>{d.department}</td><td>{d.amount}</td><td>{d.reason}</td><td>{d.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}

export default function App() {
  return <ApolloProvider client={client}><Dashboard /></ApolloProvider>
}
