// TODO: implement chart component
import React, { useMemo } from "react";
import { getTotalsByField } from '../utils/utils.ts';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  PieSectorShapeProps,
  Sector,
  Pie,
  PieChart,
} from "recharts";

const DenialChart = ({ data }) => {
    const distributionByDepartment = useMemo(() => getTotalsByField({data: data, field: "department"}), [data]);
    console.log(distributionByDepartment);

    const CustomShape = (props: PieSectorShapeProps) => {
        return <Sector {...props} fill="#8884d8" />;
    }

    return (
        <ResponsiveContainer width="100%" maxWidth={700} height={400}>
            <BarChart
                data={distributionByDepartment}
                margin={{ top: 5, right: 0, left: 0, bottom: 5 }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="department" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar
                dataKey="count"
                fill="#8884d8"
                radius={[10, 10, 0, 0]}
                />
            </BarChart>
        </ResponsiveContainer>
    )
};

export default DenialChart;
