import React from 'react';
import type { PieLabelRenderProps } from 'recharts';

type DenialsData = {
    id: string,
    department: string,
    amount: number,
    reason: string,
    date: string,
    payer: string,
}

type ChartData = {
    data: DenialsData[],
    field: keyof DenialsData,
}

const RADIAN = Math.PI / 180;

const getTotalsByField = ({data, field}: ChartData) => {
   const counts = data.reduce<Record<string, number>>((acc, item) => {
    acc[item.department] = (acc[item.department] ?? 0) + 1
    return acc
  }, {})

  return Object.entries(counts).map(([department, count]) => ({
    department,
    count,
  }));
}

const renderCustomizedLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent, name }: PieLabelRenderProps) => {
  if (cx == null || cy == null || innerRadius == null || outerRadius == null) {
    return null;
  }

  const radius = Number(outerRadius) + 20;
  const x = Number(cx) + radius * Math.cos(-midAngle * RADIAN);
  const y = Number(cy) + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text
      x={x}
      y={y}
      fill="#333"
      textAnchor={Number(x) > Number(cx) ? "start" : "end"}
      dominantBaseline="central"
      style={{ fontSize: 12, fontWeight: 500 }}
    >
      {`${name} (${((percent ?? 0) * 100).toFixed(0)}%)`}
    </text>
  );
};

export {
    getTotalsByField,
    renderCustomizedLabel
}
